import React,{Component} from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      data:[{Name: "","Highlights":[],"Cuisines":[]}],
      search :''
    }
  }
  componentDidMount() {
    console.log(123);

    axios.get("http://localhost:8000/search/city?query=chennai").then(result=>{this.setState({data:result.data});console.log(result,1)})
  }
  search = (e)=>{
    this.setState({search:e.target.value})
  }
  button = (e)=>{
    axios.get("http://localhost:8000/search/city?query="+this.state.search).then(result=>{this.setState({data:result.data});console.log(result,1)})

  }
  render() { 
    console.log(this.state);
    return ( <div>
      <div class=""><h1>Zomato Api</h1>
      <input type="text" class="search" value={this.state.search} onChange={e=>this.search(e)} placeholder="Search"/>
      <button onClick={e=>this.button(e)}>Let's Go</button></div><br/>
      {this.state.data.map(e=> <a href={e.Link}> <div className="hotel1 card" > <img src={e["Image Url"]} alt="img"/>
      <div className="hi"><h1>{e.Name}</h1>
      <h5 className="h5class">{e.Address}</h5>
      <div class="hi3">

      <div className="hi4"><h4>Highlights</h4>{e["Highlights"].map(i=><h5>&emsp;&emsp;{i}</h5>)}</div>
      <div><h4>Cuisines</h4>{e["Cuisines"].map(i=><h5>&emsp;&emsp;{i}</h5>)}</div>
      </div>
      </div>
      <div className="hi2"><br/><br/>
    <h5>Timings         : {e.Timings}</h5>
    <h5>Average Cost    : Rs.{e['Average Cost']}</h5>
    <h5>Online Delivery : {e["Online Delivery"]}</h5>
    <h5>Contact No : {e.Contact}</h5>
    <h5>Rating : <strong>{e.Rating} <span class="fa fa-star checked"/>
</strong></h5>
      </div> 
      </div></a>)}
    </div> );
  }
}

export default App;
